import { __decorate } from "tslib";
import { Component } from '@angular/core';
let PetsComponent = class PetsComponent {
    constructor(petService) {
        this.petService = petService;
        this.pets = [];
        this.status = 'ALL';
        this.filteredPets = [];
        this.selectedPet = null;
    }
    async ngOnInit() {
        this.pets = await this.petService.getPets();
        this.filterPets();
    }
    filterPets() {
        this.filteredPets =
            this.status === 'ALL' ? this.pets : this.pets.filter((pet) => pet.status === this.status);
    }
    handleStatusChange(newStatus) {
        this.status = newStatus;
        this.filterPets();
    }
    handleSave(pet) {
        Object.assign(this.selectedPet, pet);
        this.selectedPet = null;
    }
};
PetsComponent = __decorate([
    Component({
        selector: 'app-pets',
        templateUrl: './pets.component.html',
        styleUrls: ['./pets.component.css']
    })
], PetsComponent);
export { PetsComponent };
